import React , { Component } from "react";
import axios from 'axios';
import { Link , useNavigate } from "react-router-dom";
import './authentification.css'
export default class Login extends Component {
constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
   handleSubmit(e) {
     e.preventDefault();
     const { email, password } = this.state;
     console.log(email, password);
     fetch("http://localhost:5000/login-user", {
       method: "POST",
       crossDomain: true,
       headers: {
         "Content-Type": "application/json",
         Accept: "application/json",
         "Access-Control-Allow-Origin": "*",
       },
       body: JSON.stringify({
         email,
         password,
       }),
     })
       .then((res) => res.json())
       .then((data) => {
         console.log(data, "userRegister");
         if (data.status == "ok") {
           alert("login successful");
           window.localStorage.setItem("token", data.data);
           window.location.href = "./userDetails";
         }
       });
   } 
   render() {
     return (<div>
 
       <section className="auth">
         <h3 className="auth3">Authorization</h3>
         <h1>Log in to your account</h1>
         <form className="form" onSubmit={this.handleSubmit} >
           <div class="form-item">
             <input type="text" required className="inputauth" name="email"  onChange={(e) => this.setState({ email: e.target.value })}   placeholder="Your email" />
             <label className="label">Email</label>
           </div>
           <div className="form-item">
             <input type="password" required className="inputauth" name="password" onChange={(e) => this.setState({ password: e.target.value })} placeholder="Your password" />
             <label className="label">Password</label>
           </div>
           <div className="form-submit">
      
             <Link to="/Espace_superviseur" activestyles>   <button type="submit" className="button" > Log in </button>
             </Link>
                    
      
           </div>
         </form>
 
       </section>


     </div>);
   } 
}
