import React ,{useState}  from "react";
import axios from 'axios';
import { Link , useHistory } from "react-router-dom";
import './authentification.css'
const Auth = ({ setLoginUser }) => {
  const history = useHistory()
    const [user,setUser] = useState({
        name:"",
        password: ""
    })
    const handleChange = e =>{
    const {name,value} = e.target
    setUser({
    ...user,//spread operator 
    [name]:value
    })
    }

    const login =()=>{
        axios.post("http://localhost:3001/Login",user)
        .then(res=>{alert(res.data.message)
        setLoginUser(res.data.user)
    history.push("/")})
    }
    return ( <div>
 
<section className="auth">
  <h3 className="auth3">Authorization</h3>
  <h1>Log in to your account</h1>
  <form className="form">
    <div class="form-item">
      <input type="text" required className="inputauth" />
      <label className="label">Email</label>
    </div>
    <div className="form-item">
      <input type="password" required  className="inputauth" />
      <label className="label">Password</label>
    </div>
    <div className="form-submit">
      
                        <Link to="/Espace_superviseur" activestyles>   <button type="submit" className="button"> Log in </button> 
                      </Link>
                    
      
    </div>
  </form>
 
</section>


 </div>)
} 
export default Auth
