import React ,{useState}  from "react";
import axios from 'axios';
import { Link  } from "react-router-dom";
import './authentification.css'
function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [login, setLogin] = useState(false);
  const handleSubmit = (e) => {
    // set configurations
    const configuration = {
      method: "post",
      url: "https://nodejs-mongodb-auth-app.herokuapp.com/login",
      data: {
        email,
        password,
      },
    };
 // make the API call
    axios(configuration)
      .then((result) => {
        setLogin(true);
      })
      .catch((error) => {
        error = new Error();
      });
    // prevent the form from refreshing the whole page
    e.preventDefault();
    // make a popup alert showing the "submitted" text
    alert("Submited");
  }
 
    return ( <div>
 
<section className="auth">
  <h3 className="auth3">Authorization</h3>
  <h1>Log in to your account</h1>
  <form className="form" onSubmit={(e)=>handleSubmit(e)}   >
    <div class="form-item">
      <input type="text" required className="inputauth" name="email" value={email}  onChange={(e) => setEmail(e.target.value)} placeholder="Your email" />
      <label className="label">Email</label>
    </div>
    <div className="form-item">
      <input type="password" required  className="inputauth" name="password" value={password}  onChange={(e) => setPassword(e.target.value)}  placeholder="Your password" />
      <label className="label">Password</label>
    </div>
    <div className="form-submit">
      
   <Link to="/Espace_superviseur" activestyles>   <button type="submit" className="button"  onClick={(e)=>handleSubmit(e)}> Log in </button> 
      </Link>
                    
      
          </div>
          {/* display success message */}
        {login ? (
          <p className="text-success">You Are Logged in Successfully</p>
        ) : (
          <p className="text-danger">You Are Not Logged in</p>
        )} 
  </form>
 
</section>


 </div>)
} 
export default Login
